// SPDX-License-Identifier: MIT
pragma solidity 0.8.28;
import {Math} from "@openzeppelin/contracts/utils/math/Math.sol";
import {ERC20, IERC20} from "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {ReentrancyGuard} from "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import {Pausable} from "@openzeppelin/contracts/utils/Pausable.sol";
import {BaseRewardRateManager} from "./BaseRewardRateManager.sol";
import {IStakingRewards} from "./interfaces/IStakingRewards.sol";
import {WithdrawRequestsManager} from "./WithdrawRequestsManager.sol";
import {Initializable} from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import {PausableUpgradeable} from "@openzeppelin/contracts-upgradeable/utils/PausableUpgradeable.sol";
import {ReentrancyGuardUpgradeable} from "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";
import {AccessControlEnumerableUpgradeable} from "@openzeppelin/contracts-upgradeable/access/extensions/AccessControlEnumerableUpgradeable.sol";

/**
 * @title BaseStakingRewards
 * @notice A staking contract that distributes rewards to stakers over time
 * @dev Based on Synthetix StakingRewards contract with modifications
 */
contract BaseStakingRewards is 
    IStakingRewards, 
    Initializable,
    ReentrancyGuardUpgradeable, 
    PausableUpgradeable, 
    AccessControlEnumerableUpgradeable,
    BaseRewardRateManager,
    WithdrawRequestsManager 
{
    using Math for uint256;

    /* ========== STATE VARIABLES ========== */

    IERC20 public rewardsToken;
    IERC20 public stakingToken;
    uint256 public stakingCap;

    uint256 private _totalSupply;
    mapping(address => uint256) private _balances;
    mapping(address => uint256) public userLastActionTime;
    mapping(address => uint256) public userCumulativeRewards;
    mapping(address => uint256) private _lockedBalances;

    bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");
    bytes32 public constant UNPAUSER_ROLE = keccak256("UNPAUSER_ROLE");
    bytes32 public constant CAP_MANAGER_ROLE = keccak256("CAP_MANAGER_ROLE");
    bytes32 public constant EMERGENCY_ROLE = keccak256("EMERGENCY_ROLE");
    bytes32 public constant RATE_MANAGER_ROLE = keccak256("RATE_MANAGER_ROLE");

    constructor() {
        _disableInitializers();
    }

    struct InitParams {
        // Role addresses
        address owner;
        address pauser;
        address unpauser;
        address capManager; 
        address rateManager;
        address emergency;
        // Contract addresses
        address rewardsToken;
        address stakingToken;
    }

    function initialize(InitParams calldata params) public initializer {
        // Validate initialization parameters
        require(params.owner != address(0), "Owner address cannot be zero");
        require(params.pauser != address(0), "Pauser address cannot be zero");
        require(params.unpauser != address(0), "Pauser address cannot be zero");
        require(params.capManager != address(0), "Cap manager address cannot be zero");
        require(params.emergency != address(0), "Emergency address cannot be zero");
        require(params.rateManager != address(0), "Rate manager address cannot be zero");
        require(params.rewardsToken != address(0), "Rewards token address cannot be zero");
        require(params.stakingToken != address(0), "Staking token address cannot be zero");

        __ReentrancyGuard_init();
        __Pausable_init();
        __AccessControlEnumerable_init();
        __BaseRewardRateManager_init();

        _setRoleAdmin(EMERGENCY_ROLE, DEFAULT_ADMIN_ROLE);
        _setRoleAdmin(PAUSER_ROLE, DEFAULT_ADMIN_ROLE);
        _setRoleAdmin(UNPAUSER_ROLE, DEFAULT_ADMIN_ROLE);
        _setRoleAdmin(CAP_MANAGER_ROLE, DEFAULT_ADMIN_ROLE);
        _setRoleAdmin(RATE_MANAGER_ROLE, DEFAULT_ADMIN_ROLE);

        _grantRole(DEFAULT_ADMIN_ROLE, params.owner);
        _grantRole(PAUSER_ROLE, params.pauser);
        _grantRole(UNPAUSER_ROLE, params.unpauser);
        _grantRole(CAP_MANAGER_ROLE, params.capManager);
        _grantRole(EMERGENCY_ROLE, params.emergency);
        _grantRole(RATE_MANAGER_ROLE, params.rateManager);

        rewardsToken = IERC20(params.rewardsToken);
        stakingToken = IERC20(params.stakingToken);
    }

    /* ========== VIEWS ========== */

    /**
     * @notice Get the total amount of tokens staked in the contract
     * @return The total supply of staked tokens
     */
    function totalSupply() external view returns (uint256) {
        return _totalSupply;
    }

    /**
     * @notice Get the staked balance of a specific account
     * @param account The address to check the balance for
     * @return The amount of tokens staked by the account
     */
    function balanceOf(address account) external view returns (uint256) {
        return _balances[account];
    }

    /**
     * @notice Calculate the total rewards earned by an account
     * @dev Combines accumulated rewards with current pending rewards
     * @param account The address to calculate rewards for
     * @return The total amount of rewards earned
     */
    function earned(address account) public view returns (uint256) {
        return userCumulativeRewards[account] + _calculateRewards(account);
    }

    /* ========== MUTATIVE FUNCTIONS ========== */

    /**
     * @notice Stake tokens in the contract
     * @dev Updates rewards before staking and enforces staking cap
     * @param amount The amount of tokens to stake
     */
    function stake(uint256 amount) external nonReentrant whenNotPaused {
        require(amount > 0, "Cannot stake 0");
        require(_totalSupply + amount <= stakingCap, "Staking cap reached");

        userCumulativeRewards[msg.sender] = earned(msg.sender);

        _totalSupply += amount;
        _balances[msg.sender] += amount;
        userLastActionTime[msg.sender] = block.timestamp;

        SafeERC20.safeTransferFrom(stakingToken, msg.sender, address(this), amount);
        emit Staked(msg.sender, amount);
    }

    /**
     * @notice Request to withdraw staked tokens
     * @dev Creates a withdrawal request that can be executed after cooldown
     * @param amount The amount of tokens to withdraw
     * @return withdrawId The ID of the withdrawal request
     */
    function requestWithdraw(uint256 amount) 
        external 
        nonReentrant
        whenNotPaused
        returns (uint256) 
    {
        require(amount <= _balances[msg.sender] - _lockedBalances[msg.sender], "Insufficient withdrawable balance");

        userCumulativeRewards[msg.sender] = earned(msg.sender);
        _lockedBalances[msg.sender] += amount;
        userLastActionTime[msg.sender] = block.timestamp;

        return _requestWithdraw(amount);
    }

    /**
     * @notice Execute a pending withdrawal request
     * @param withdrawId The ID of the withdrawal request to execute
     */
    function executeWithdraw(uint256 withdrawId) 
        external 
        nonReentrant 
        whenNotPaused
    {
        uint256 amount = _executeWithdraw(msg.sender, withdrawId);

        _totalSupply -= amount;
        _balances[msg.sender] -= amount;
        _lockedBalances[msg.sender] -= amount;

        SafeERC20.safeTransfer(stakingToken, msg.sender, amount);
        emit Withdrawn(msg.sender, amount);
    }

    /**
     * @notice Claim accumulated rewards
     * @dev Converts mETH rewards to ETH before transfer
     */
    function getReward() 
        public 
        nonReentrant 
        whenNotPaused
    {
        uint256 totalReward = earned(msg.sender);

        if (totalReward > 0) {
            userCumulativeRewards[msg.sender] = 0;
            userLastActionTime[msg.sender] = block.timestamp;

            SafeERC20.safeTransfer(rewardsToken, msg.sender, totalReward);
            emit RewardPaid(msg.sender, totalReward);
        }
    }

    /* ========== RESTRICTED FUNCTIONS ========== */

    /**
     * @notice Emergency function to recover tokens sent to the contract
     * @dev Can only be called by accounts with EMERGENCY_ROLE
     * @param tokenAddress Address of the token to recover
     * @param tokenAmount Amount of tokens to recover
     */
    function recoverTokens(address tokenAddress, uint256 tokenAmount)
        external
        onlyRole(EMERGENCY_ROLE)
    {

        uint256 currentBalance = IERC20(tokenAddress).balanceOf(address(this));
        if (tokenAddress == address(stakingToken)) {
            require(currentBalance - _totalSupply >= tokenAmount, "Overdrawn staking tokens");
        } else {
            require(currentBalance >= tokenAmount, "Overdrawn other tokens");
        }

        SafeERC20.safeTransfer(IERC20(tokenAddress), msg.sender, tokenAmount);
        emit Recovered(tokenAddress, tokenAmount);
    }

    /**
     * @notice Set the maximum amount of tokens that can be staked
     * @dev Only CAP_MANAGER_ROLE can call this function
     * @param _stakingCap The new staking cap value (0 for no cap)
     */
    function setStakingCap(uint256 _stakingCap) 
        external 
        onlyRole(CAP_MANAGER_ROLE)
        whenNotPaused
    {
        stakingCap = _stakingCap;
        emit StakingCapUpdated(_stakingCap);
    }

    /**
     * @notice Pause the contract
     * @dev Only PAUSER_ROLE can call this function
     */
    function pause() external onlyRole(PAUSER_ROLE) {
        _pause();
    }

    /**
     * @notice Unpause the contract
     * @dev Only UNPAUSER_ROLE can call this function
     */
    function unpause() external onlyRole(UNPAUSER_ROLE) {
        _unpause();
    }

    /**
     * @notice Propose a new reward rate that will take effect after delay
     * @dev Only RATE_MANAGER_ROLE can call this function
     * @param newRate The new reward rate in basis points (100 = 1%)
     */
    function proposeRewardRate(uint256 newRate) 
        external 
        onlyRole(RATE_MANAGER_ROLE)
        whenNotPaused 
    {
        _proposeRewardRate(newRate);
    }

    /* ========== INTERNAL FUNCTIONS ========== */

    /**
     * @dev Calculate rewards for an account based on stake amount and duration
     * @param account The address to calculate rewards for
     * @return The amount of rewards earned
     */
    function _calculateRewards(address account) internal view returns (uint256) {
        uint256 activeBalance = _balances[account] - _lockedBalances[account];
        if (activeBalance == 0) return 0;

        return _calculateRewardRateBySnapshot(activeBalance, userLastActionTime[account], block.timestamp);
    }
}

