// SPDX-License-Identifier: MIT
pragma solidity 0.8.28;

import {Math} from "@openzeppelin/contracts/utils/math/Math.sol";
import {Initializable} from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

contract BaseRewardRateManager is Initializable {    
    uint256 public constant RATE_CHANGE_DELAY = 24 hours;
    uint256 public constant MAX_RATE = 10000; // 100% in basis points

    uint256 public constant rewardRate = 210; // 2.1% APR in basis points (simple interest)

    event RewardRateChangeProposed(uint256 newRate, uint256 effectiveTime);

    struct RateCheckpoint {
        uint256 timestamp;
        uint256 rate;
    }

    RateCheckpoint[] public rateHistory;

    constructor() {
        _disableInitializers();
    }

    function __BaseRewardRateManager_init() internal onlyInitializing {
        // Initialize rate history with default rate
        rateHistory.push(RateCheckpoint(block.timestamp, rewardRate));
    }

    /**
     * @notice Internal function to propose a new reward rate
     * @param newRate The new reward rate in basis points (100 = 1%)
     */
    function _proposeRewardRate(uint256 newRate) internal virtual {
        require(newRate <= MAX_RATE, "Rate cannot exceed 100%");
        uint256 effectiveTime = block.timestamp + RATE_CHANGE_DELAY;

        // Add new rate checkpoint with future timestamp
        rateHistory.push(RateCheckpoint(effectiveTime, newRate));

        emit RewardRateChangeProposed(newRate, effectiveTime);
    }

    function _calculateRewardRateBySnapshot(uint256 balance, uint256 startTime, uint256 endTime) internal view returns (uint256) {        
        // Return 0 if no rate history or if earliest rate is after endTime
        if (rateHistory.length == 0 || rateHistory[0].timestamp > endTime) return 0;

        // If startTime is after the latest rate, use the latest rate for calculation
        if (startTime >= rateHistory[rateHistory.length - 1].timestamp) {
            uint256 duration = endTime - startTime;
            return (balance * rateHistory[rateHistory.length - 1].rate * duration) / (365 days * 10000);
        }

        uint256 totalRewards = 0;
        uint256 currentTime = startTime;

        for (uint256 i = 0; i < rateHistory.length; i++) {
            // skip past rate changes
            if (rateHistory[i].timestamp < startTime) continue;
            // get the rate for the period
            uint256 currentRate = i == 0 ? rateHistory[i].rate : rateHistory[i-1].rate;
            // get the end of the period
            uint256 periodEnd = Math.min(endTime, rateHistory[i].timestamp);
            // calculate the duration of the period (in seconds)
            uint256 periodDuration = periodEnd - currentTime;
            // calculate the rewards for the period
            totalRewards += (balance * currentRate * periodDuration);
            // update the current time to the end of the period
            currentTime = periodEnd;
            // Early exit if we've reached the end time
            if (periodEnd == endTime) break;
        }
        // Handle the last period if needed
        if (currentTime < endTime) {
            uint256 periodDuration = endTime - currentTime;
            totalRewards += (balance * rateHistory[rateHistory.length - 1].rate * periodDuration);
        }

        return totalRewards / (365 days * 10000);
    }
}

