// SPDX-License-Identifier: MIT
pragma solidity 0.8.28;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/**
 * @title IStakingRewards
 * @notice Interface for the StakingRewards contract
 * @dev Defines core staking functionality for depositing tokens and earning rewards
 */
interface IStakingRewards {
    /* ========== EVENTS ========== */

    /**
     * @notice Emitted when a user stakes tokens
     * @param user The address of the user who staked
     * @param amount The amount of tokens staked
     */
    event Staked(address indexed user, uint256 amount);

    /**
     * @notice Emitted when a user withdraws staked tokens
     * @param user The address of the user who withdrew
     * @param amount The amount of tokens withdrawn
     */
    event Withdrawn(address indexed user, uint256 amount);

    /**
     * @notice Emitted when rewards are paid to a user
     * @param user The address of the user who received rewards
     * @param reward The amount of rewards paid
     */
    event RewardPaid(address indexed user, uint256 reward);

    /**
     * @notice Emitted when staking cap is updated
     * @param newCap The new staking cap value
     */
    event StakingCapUpdated(uint256 newCap);

    /**
     * @notice Emitted when tokens are recovered from the contract
     * @param token The address of the recovered token
     * @param amount The amount recovered
     */
    event Recovered(address token, uint256 amount);

    /* ========== VIEW FUNCTIONS ========== */

    /**
     * @notice Get the staked balance of an account
     * @param account Address to check balance for
     * @return Amount of tokens staked by the account
     */
    function balanceOf(address account) external view returns (uint256);

    /**
     * @notice Calculate total rewards earned by an account
     * @param account Address to check rewards for
     * @return Total amount of rewards earned
     */
    function earned(address account) external view returns (uint256);

    /**
     * @notice Get the token used for rewards
     * @return Address of the rewards token
     */
    function rewardsToken() external view returns (IERC20);

    /**
     * @notice Get total amount of tokens staked in the contract
     * @return Total supply of staked tokens
     */
    function totalSupply() external view returns (uint256);

    /**
     * @notice Get the token used for staking
     * @return Address of the staking token
     */
    function stakingToken() external view returns (IERC20);

    /**
     * @notice Get the current staking cap
     * @return Current maximum amount that can be staked
     */
    function stakingCap() external view returns (uint256);

    /**
     * @notice Get the last time an account staked
     * @param account Address to check
     * @return Timestamp of last stake
     */
    function userLastActionTime(address account) external view returns (uint256);

    /* ========== MUTATIVE FUNCTIONS ========== */

    /**
     * @notice Claim accumulated rewards
     */
    function getReward() external;

    /**
     * @notice Stake tokens in the contract
     * @param amount Amount of tokens to stake
     */
    function stake(uint256 amount) external;

    /**
     * @notice Request to withdraw staked tokens
     * @param amount Amount of tokens to withdraw
     * @return withdrawId Unique identifier for the withdrawal request
     */
    function requestWithdraw(uint256 amount) external returns (uint256);

    /**
     * @notice Execute a pending withdrawal request
     * @param withdrawId ID of the withdrawal request to execute
     */
    function executeWithdraw(uint256 withdrawId) external;
}

src/WithdrawRequestsManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.28;

/**
 * @title WithdrawRequestsManager
 * @notice Manages withdrawal requests with cooldown periods
 */
contract WithdrawRequestsManager {
    // Constants
    uint256 public constant COOLDOWN_PERIOD = 24 hours;

    // Structs
    struct WithdrawRequest {
        uint256 amount;
        uint256 requestTime;
        bool executed;
    }

    // State variables
    mapping(address => mapping(uint256 => WithdrawRequest)) public withdrawRequests;
    mapping(address => uint256) public nextWithdrawId;

    // Events
    event WithdrawRequested(address indexed user, uint256 indexed withdrawId, uint256 amount, uint256 requestTime);
    event WithdrawExecuted(address indexed user, uint256 indexed withdrawId, uint256 amount);

    /**
     * @notice Checks if a withdrawal request is ready to be executed
     * @param user Address of the user
     * @param withdrawId ID of the withdrawal request
     * @return bool Whether the withdrawal can be executed
     */
    function canExecuteWithdraw(address user, uint256 withdrawId) public view returns (bool) {
        WithdrawRequest memory request = withdrawRequests[user][withdrawId];
        return 
            !request.executed &&
            (block.timestamp >= request.requestTime + COOLDOWN_PERIOD);
    }

    /**
     * @notice Gets the details of a withdrawal request
     * @param user Address of the user
     * @param withdrawId ID of the withdrawal request
     * @return amount Amount requested
     * @return requestTime Time of the request
     * @return executed Whether the request has been executed
     * @return readyToExecute Whether the request is ready to execute
     */
    function getWithdrawRequest(address user, uint256 withdrawId) 
        external 
        view 
        returns (
            uint256 amount,
            uint256 requestTime,
            bool executed,
            bool readyToExecute
        ) 
    {
        WithdrawRequest memory request = withdrawRequests[user][withdrawId];
        return (
            request.amount,
            request.requestTime,
            request.executed,
            canExecuteWithdraw(user, withdrawId)
        );
    }

    function _requestWithdraw(uint256 amount) internal returns (uint256) {
        require(amount > 0, "Amount must be greater than 0");

        uint256 withdrawId = nextWithdrawId[msg.sender]++;
        withdrawRequests[msg.sender][withdrawId] = WithdrawRequest({
            amount: amount,
            requestTime: block.timestamp,
            executed: false
        });

        emit WithdrawRequested(msg.sender, withdrawId, amount, block.timestamp);
        return withdrawId;
    }

    function _executeWithdraw(address user, uint256 withdrawId) internal returns (uint256) {
        require(canExecuteWithdraw(user, withdrawId), "Withdrawal not ready or already executed");

        WithdrawRequest storage request = withdrawRequests[user][withdrawId];
        uint256 amount = request.amount;
        request.executed = true;

        emit WithdrawExecuted(user, withdrawId, amount);
        return amount;
    }
}

